const axios = require("axios");

import FullAvisComponent from '@/components/FullAvisComponent.vue';
export default {
  name: 'AvisView',
  components: {
    FullAvisComponent
  },

  data() {
    return {
      dataAvis: []
    };
  },

  beforeCreate() {
    axios.get(this.$store.state.URLAPI + '/avis/' + this.$route.params.id).then(response => this.dataAvis = response.data[0]);
